var myFirebaseRef = new Firebase("https://sweltering-inferno-9361.firebaseio.com/");
var authData = myFirebaseRef.getAuth();
var userLoginId = authData.uid;
var restaurantAddress = null;
var restaurantState = null;
function findRestaurant(restaurantInfo){
	myFirebaseRef.child("restaurant/"+restaurantInfo).on("value", function(snapshot){
		var restaurantPost = snapshot.val();
		if(restaurantPost === restaurantInfo){
			restaurantAddress = restaurantPost.Address;
			restaurantState = restaurantPost.State; 
			console.log(restaurantPost.Address);
			console.log(restaurantPost.State);
		}
	});
}
myFirebaseRef.child("users/UserId: "+userLoginId).on("value", function(snapshot){
	var findPost = snapshot.val();
	if(findPost.UserId === userLoginId){
		console.log("Succesfully Found");
		console.log(findPost.restaurant);
		findRestaurant(findPost.restaurant);
	} else {
		console.log("Not Successfully found");
	} 
},  function(errorObject){
	console.log(errorObject);
});

